$(document).ready(function () {

    var subjects;
    var works;
    var employees;

    if ($("#Type").val() != "") {
        $("#subject_block").fadeIn(250);
        if ($("#Type").val() == 4) {
            $("#add-material-form").prop('action', '/EducationalMaterials/AddLabJournal');
            getLaboratorySubjects()
            $("#Subject").autocomplete({
                source: function (request, response) {
                    response(subjects);
                },
                delay: 10,
                minLength: 0
            });
            $("#Name").prop('disabled', true);
            $("#Name").val("Лабораторная работа. " + $("#Work").val());
        } else {
            $("#add-material-form").prop('action', '/EducationalMaterials/AddMaterial');
            getAllSubjects();
            $("#Subject").autocomplete({
                source: function (request, response) {
                    var results = $.ui.autocomplete.filter(subjects, request.term);
                    if (results.length == 0)
                        response(subjects.slice(0, 12));
                    else
                        response(results.slice(0, 12));
                    
                },
                delay: 200,
                minLength: 0
            });
        }

    }
    if ($("#Subject").val() != "") {
        getEmployees();
        $("#general_block").fadeIn(250);
        $("#upload_block").fadeIn(250);
        if ($("#Type").val() == 4) {

            $("#laboratory_block").fadeIn(250);
            getWorks($("#Subject").val());
            $("#Work").autocomplete({
                source: function (request, response) {
                    var results = $.ui.autocomplete.filter(works, request.term);
                    if (results.length == 0)
                        response(works.slice(0, 10));
                    else
                        response(results.slice(0, 10));
                },
                delay: 100,
                minLength: 0
            });
        }
    }
        

    $("#Type").on('input', function () {
        $("#general_block").fadeOut(250);
        $("#laboratory_block").fadeOut(250);
        $("#upload_block").fadeOut(250);
        $("#subject_block").fadeIn(250);
        $("#Subject").val(null);
        if ($("#Type").val() == 4) {
            $("#add-material-form").prop('action', '/EducationalMaterials/AddLabJournal');
            getLaboratorySubjects();
            $("#Subject").autocomplete({
                source: function (request, response) {
                    response(subjects);
                },
                delay: 10,
                minLength: 0
            });
            $("#Name").prop('disabled', true);
            $("#Name").val("Лабораторная работа. " + $("#Work").val());
        }
        else {
            $("#add-material-form").prop('action', '/EducationalMaterials/AddMaterial');
            $("#Name").prop('disabled', false);
            $("#Name").val(null);
            getAllSubjects();
            $("#Subject").autocomplete({
                source: function (request, response) {
                    var results = $.ui.autocomplete.filter(subjects, request.term);
                    if (results.length == 0)
                        response(subjects.slice(0, 12));
                    else
                        response(results.slice(0, 12));
                    
                },
                delay: 200,
                minLength: 0
            });
        }
    });

    $("#Subject").autocomplete({
        close: function () {
            $("#general_block").fadeIn(250);
            if ($("#Type").val() == 4) {
                getWorks($("#Subject").val());
                $("#laboratory_block").fadeIn(250);
                $("#Work").autocomplete({
                    source: function (request, response) {
                        var results = $.ui.autocomplete.filter(works, request.term);
                        if (results.length == 0)
                            response(works.slice(0, 10));
                        else
                            response(results.slice(0, 10));
                    },
                    delay: 100,
                    minLength: 0
                });
            }
            else
                $("#laboratory_block").fadeOut(250);
            $("#upload_block").fadeIn(250);
            getEmployees();
        }
    });

    $("#EmployeeFullName").autocomplete({
        source: function (request, response) {
            var results = $.ui.autocomplete.filter(employees, request.term);
            if (results.length == 0)
                response(employees.slice(0, 10));
            else
                response(results.slice(0, 10));
        },
        delay: 100,
        minLength: 0
    });

    $("#Work").change(function () {
        $("#Name").val("Лабораторная работа. " + $("#Work").val());
    });


    function getAllSubjects() {
        $.ajax({
            type: "GET",
            url: '/api/university/subjects',
            dataType: "json",
            data: { isLaboratory: false },
            error: function () {
                console.log("Failed to load subjects");
            },
            success: function (res) {
                console.log(res);
            }
        }).done(function (res) {
            subjects = res;
        });
    };

    function getLaboratorySubjects() {
        $.ajax({
            type: "GET",
            url: '/api/university/subjects',
            dataType: "json",
            data: { isLaboratory: true },
            error: function () {
                console.log("Failed to load laboratory subjects");
            },
            success: function (res) {
                console.log(res);
            }
        }).done(function (res) {
            subjects = res;
        });
    };

    function getWorks(subject) {
        $.ajax({
            type: "GET",
            url: '/api/university/works',
            dataType: "json",
            data: { subject: subject },
            error: function () {
                console.log("Failed to load emplyees");
            },
            success: function (res) {
                console.log(res);
            }
        }).done(function (res) {
            works = res;
        });
    };

    function getEmployees() {
        $.ajax({
            type: "GET",
            url: '/api/university/employees',
            dataType: "json",
            error: function () {
                console.log("Failed to load emplyees");
            },
            success: function (res) {
                console.log(res);
            }
        }).done(function (res) {
            employees = res;
        });
    };
});


$(document).ready(function () {
    $.uploadPreview({
        input_field: "#uploads",
        preview_box: "#uploads-preview",
        label_field: "#uploads-label"
    });
    $("#uploads-preview").disableSelection();
    $("form").submit(function (event) {
        $("#Name").prop('disabled', false);
        CreateQuery();
    });
    var el = document.getElementById('uploads-preview');
    var sortable = Sortable.create(el, {
        swapThreshold: 1,
        animation: 150,
        ghostClass: "ghost"
    });
    function CreateQuery(event, ui) {
        var list = [];
        $("#uploads-preview").children().each(function (index) {
            var fileName = $(this).find(".thumb-name").text();
            if (fileName != undefined)
                list.push(fileName);
        });

        console.log(list);
        $("#query").val(list.toString());
    }
});

(function ($) {
    $.extend({
        uploadPreview: function (options) {

            // Options + Defaults
            var settings = $.extend({
                input_field: ".image-input",
                preview_box: ".image-preview",
                label_field: ".image-label",
                label_default: "Choose File",
                label_selected: "Change File",
                no_label: false,
                success_callback: null,
            }, options);

            // Check if FileReader is available
            if (window.File && window.FileList && window.FileReader) {
                if (typeof ($(settings.input_field)) !== 'undefined' && $(settings.input_field) !== null) {
                    $(settings.input_field).change(function () {
                        var files = this.files;
                        

                        if (files.length > 0) {
                            $(settings.preview_box).empty();
                            for (let i = 0; i < files.length; i++) {
                                var file = files[i];
                                var reader = new FileReader();
                                

                                // Load file
                                reader.addEventListener("load", function (event) {
                                    var loadedFile = event.target;

                                    // Check format
                                    if (file.type.match('image')) {
                                        // Image

                                        $('<div class="file-sortable ui-state-default col-6 col-md-4 col-lg-2 m-1" id="sortable-item' + i + '">' +                                                         
                                                    '<img src="' + loadedFile.result + '" class="img-thumbnail"/>' +
                                                    '<span class="thumb-name">' + files[i].name + '</span>' +                          
                                          '</div>'
                                        ).appendTo($(settings.preview_box));

                                    } else if (file.type.match('audio')) {
                                        // Audio
                                        $(settings.preview_box).html("<audio controls><source src='" + loadedFile.result + "' type='" + file.type + "' />Your browser does not support the audio element.</audio>");
                                    } else {
                                        alert("This file type is not supported yet.");
                                    }
                                });

                                if (settings.no_label == false) {
                                    // Change label
                                    $(settings.label_field).html(settings.label_selected);
                                }

                                // Read the file
                                reader.readAsDataURL(file);

                                // Success callback function call
                                if (settings.success_callback) {
                                    settings.success_callback();
                                }

                            }
                        } else {
                            if (settings.no_label == false) {
                                // Change label
                                $(settings.label_field).html(settings.label_default);
                            }

                            // Clear background
                            $(settings.preview_box).css("background-image", "none");

                            // Remove Audio
                            $(settings.preview_box + " audio").remove();
                        }
                    });
                }
            } else {
                alert("You need a browser with file reader support, to use this form properly.");
                return false;
            }
        }
    });
})(jQuery);

